def count_freq(s):
    dic = {}
    for i in list(s):
        if i in dic:
            dic[i]+=1
        else:
            dic[i] =1
    return dic

d = count_freq('leet')
print(d)

def check_one(d):
    if 1 in d.values():
        print("let us find!")
        for let, cnt in d.items():
            if cnt == 1:
                the_let = let
                print(the_let)
                
                return the_let
    else:
        print('No unique.')

print(check_one(d))
